
input = DATA.read.chomp.split('')

# part 1
puts input.map { |p| p == '(' ? 1 : -1 }.reduce(:+)
# => 232

# part 2
input.each_with_object([]) do |p, memo|
  memo << (p == '(' ? 1 : -1)

  if memo.reduce(:+) == -1
    puts memo.size
    exit
  end
end
# => 1783

