$grid = DATA.readlines(chomp: true).map { _1.split('') }

XMAS = /XMAS|SAMX/
MAS  = /MAS|SAM/

def grid
  $grid.each_with_index do |row, y|
    row.each_with_index do |column, x|
      yield(y, x)
    end
  end
end

def window(row, column, size:)
  (0...size).map do |y|
    (0...size).map do |x|
      $grid[row + y][column + x] rescue nil
    end
  end
end

def x_scan(window, pattern: XMAS)
  window.map(&:join).count { _1[pattern] }
end

def y_scan(window, pattern: XMAS)
  window.transpose.map(&:join).count { _1[pattern] }
end

def d_scan(window, pattern: XMAS)
  d1 = 1 if (0...window.size).map { |i| window[i][i] }.join[pattern]
  d2 = 1 if (0...window.size).map { |i| window.map(&:reverse)[i][i] }.join[pattern]

  [d1, d2].compact.sum
end

puts 'Part 1'
@xmas_count = 0

grid do |y, x|
  window = window(y, x, size: 4)

  @xmas_count += x_scan(window) if (y % window.size).zero? # every 4th row
  @xmas_count += y_scan(window) if (x % window.size).zero? # every 4th column
  @xmas_count += d_scan(window)
end

p xmas: @xmas_count
# => 2591


puts 'Part 2'
@mas_count = 0

grid do |y, x|
  # only count when we have two diagonals (forming an "X" with MAS)
  if d_scan(window(y, x, size: 3), pattern: MAS) == 2
    @mas_count += 1
  end
end

p mas: @mas_count
# => 1880

__END__
